import React, { Component } from "react";
import "./App.css";
import { List, Avatar } from "antd";

import Nav from "./Nav";

class HomePage extends Component {
  // J1) --1)------Le state ThemesList reçoit les données global de l'API---------------------
  constructor(props) {
    super(props);
    this.state = {
      ThemesList: []
      // J1) --1)------ Le state ThemesList reçoit les données global de l'API-------------------
    };
  }
  // J1) < 2 -------------------------------------------------------------------------------
  componentDidMount() {
    console.log("Dans mon component did mount");
    // Here You need to fetch the data from the news Api
    fetch(
      "https://newsapi.org/v2/sources?language=fr&country=fr&apiKey=04a7c7327e6a4bb78b484f0454597596"
    ).then(function(data) {
      console.log(data);
    });
    // Then, you need to update your ThemesList state
  }

  render() {
    const data = [
      {
        title: "Ant Design Title 1"
      },
      {
        title: "Ant Design Title 2"
      },
      {
        title: "Ant Design Title 3"
      },
      {
        title: "Ant Design Title 4"
      }
    ];

    return (
      <div>
        <Nav />
        <div className="Banner" />
        <div className="HomeThemes">
          <List
            itemLayout="horizontal"
            dataSource={data}
            renderItem={item => (
              <List.Item>
                <List.Item.Meta
                  avatar={
                    <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                  }
                  title={<a href="https://ant.design">{item.title}</a>}
                  description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                />
              </List.Item>
            )}
          />
        </div>
      </div>
    );
  }
}

export default HomePage;
