import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, Icon, Modal } from 'antd';
import Nav from './Nav';

const { Meta } = Card;

function ThemesArticles(props) {
	// constructor(props) {
	// 	super(props);
	// 	this.state = {
	// 		themeArticles: [],
	// 		visible: false
	// 	};
	// }
	const [ themeArticles, setThemeArticles ] = useState([]);
	const [ visible, setVisible ] = useState(false);

	const showModal = (title, content) => {
		this.setState({
			visible: true,
			title: title,
			content: content
		});
	};

	const handleOk = (e) => {
		console.log(e);
		setVisible(false);
	};

	const handleCancel = (e) => {
		console.log(e);
		setVisible(false);
	};

	useEffect(
		() => {
			var source = props.match.params.id;
			fetch(
				`https://newsapi.org/v2/top-headlines?language=fr&sources=${source}&apiKey=04a7c7327e6a4bb78b484f0454597596`
			)
				.then((response) => response.json())
				.then((data) => setThemeArticles(data.articles));
		},
		[ themeArticles ]
	);
	// componentDidMount() {
	// 	// You need to catch the id comming from the homePage
	// 	var ctx = this;
	// 	var source = this.props.match.params.id;
	// 	fetch(
	// 		`https://newsapi.org/v2/top-headlines?language=fr&sources=${source}&apiKey=04a7c7327e6a4bb78b484f0454597596`
	// 	)
	// 		.then((response) => {
	// 			return response.json();
	// 		})
	// 		.then((data) => {
	// 			ctx.setState(() => ({
	// 				themeArticles: data.articles
	// 			}));
	// 			console.log(data.articles);
	// 		});
	// }

	var articlesList = themeArticles.map((article, index) => {
		return (
			<div style={{ display: 'flex', justifyContent: 'center' }} key={index}>
				<Card
					style={{
						width: 300,
						margin: '15px',
						display: 'flex',
						flexDirection: 'column',
						justifyContent: 'space-between'
					}}
					cover={<img alt="example" src={article.urlToImage} />}
					actions={[
						<Icon
							type="read"
							key="ellipsis2"
							onClick={() => showModal(article.title, article.description)}
						/>,
						<Icon type="like" key="ellipsis" />
					]}
				>
					<Meta title={article.title} description={article.description} />
				</Card>
				<Modal
					title={this.state.title}
					visible={this.state.visible}
					onOk={this.handleOk}
					onCancel={this.handleCancel}
				>
					<p>{this.state.content}</p>
				</Modal>
			</div>
		);
	});
	return (
		<div>
			<Nav />
			<div className="Banner" />
			<div className="Card">{articlesList}</div>
		</div>
	);
}

export default ThemesArticles;
