import React, { Component } from 'react';
import './App.css';
import { Input, Button } from 'antd';
import { Redirect } from 'react-router-dom';

class Login extends Component {
	constructor(props) {
		super(props);
		this.signUp = this.signUp.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.state = {
			signUp_first_name: '',
			signUp_last_name: '',
			signUp_email: '',
			signUp_password: '',
			login_email: '',
			login_password: '',
			isUserExist: false
		};
	}
	handleChange(event) {
		this.setState({ [event.target.name]: event.target.value });
	}

	signUp(event) {
		event.preventDefault();
		fetch(`http://localhost:3000/signup`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
			body: `first_name=${this.state.signUp_first_name}&last_name=${this.state.signUp_last_name}&email=${this
				.state.signUp_email}&password=${this.state.signUp_password}`
		})
			.then(function(response) {
				console.log("je suis dans ma reucp de l'objet json a remettre en json");
				//return response.json;
			})
			.then(function(data) {
				console.log('user logué');
				this.setState(() => ({ isUserExist: true }));
			})
			.catch((err) => {
				console.log('erroroooor');
			});
	}

	login(event) {
		event.preventDefault();
		fetch(`http://localhost:3000/signin?email=${this.state.login_email}&password=${this.state.login_password}`)
			.then((response) => response.json())
			.then(function(data) {
				console.log('user logué', data);
			})
			.catch((err) => {
				console.log('erroroooor');
			});
	}

	render() {
		return (
			<div className="Login-page">
				{/* SIGN-UP */}
				<div className="Sign">
					<Input
						className="Login-input"
						name="signUp_first_name"
						placeholder="arthur"
						onChange={(e) => this.handleChange(e)}
						value={this.state.signUp_first_name}
					/>
					<Input
						className="Login-input"
						name="signUp_last_name"
						placeholder="Lacapsule"
						onChange={(e) => this.handleChange(e)}
						value={this.state.signUp_last_name}
					/>
					<Input
						className="Login-input"
						name="signUp_email"
						placeholder="arthur@lacapsule.com"
						onChange={(e) => this.handleChange(e)}
						value={this.state.signUp_email}
					/>
					<Input.Password
						className="Login-input"
						name="signUp_password"
						placeholder="password"
						onChange={(e) => this.handleChange(e)}
						value={this.state.signUp_password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary" onClick={(e) => this.signUp(e)}>
						Sign-in
					</Button>
				</div>

				{/* SIGN-IN */}
				<div className="Sign">
					<Input
						className="Login-input"
						name="login_email"
						placeholder="arthur@lacapsule.com"
						onChange={(e) => this.handleChange(e)}
						value={this.state.login_email}
					/>
					<Input.Password
						className="Login-input"
						name="login_password"
						placeholder="password"
						onChange={(e) => this.handleChange(e)}
						value={this.state.login_password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary" onClick={(e) => this.login(e)}>
						Sign-in
					</Button>
				</div>
			</div>
		);
	}
}

export default Login;
