import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, Icon, Modal } from 'antd';
import Nav from './Nav';

const { Meta } = Card;

class ThemesArticles extends Component {
	constructor(props) {
		super(props);
		this.state = {
			ThemeArticles: [],
			visible: false
		};
	}

	showModal = (title, content) => {
		this.setState({
			visible: true,
			title: title,
			content: content
		});
	};

	handleOk = (e) => {
		console.log(e);
		this.setState({
			visible: false
		});
	};

	handleCancel = (e) => {
		console.log(e);
		this.setState({
			visible: false
		});
	};

	componentDidMount() {
		// You need to catch the id comming from the homePage
		var ctx = this;
		var source = this.props.match.params.id;
		fetch(
			`https://newsapi.org/v2/top-headlines?language=fr&sources=${source}&apiKey=04a7c7327e6a4bb78b484f0454597596`
		)
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				ctx.setState(() => ({
					ThemeArticles: data.articles
				}));
				console.log(data.articles);
			});
	}

	render() {
		var ArticlesList = this.state.ThemeArticles.map((article, index) => {
			return (
				<div style={{ display: 'flex', justifyContent: 'center' }} key={index}>
					<Card
						style={{
							width: 300,
							margin: '15px',
							display: 'flex',
							flexDirection: 'column',
							justifyContent: 'space-between'
						}}
						cover={<img alt="example" src={article.urlToImage} />}
						actions={[
							<Icon
								type="read"
								key="ellipsis2"
								onClick={() => this.showModal(article.title, article.description)}
							/>,
							<Icon type="like" key="ellipsis" />
						]}
					>
						<Meta title={article.title} description={article.description} />
					</Card>
					<Modal
						title={this.state.title}
						visible={this.state.visible}
						onOk={this.handleOk}
						onCancel={this.handleCancel}
					>
						<p>{this.state.content}</p>
					</Modal>
				</div>
			);
		});
		return (
			<div>
				<Nav />
				<div className="Banner" />
				<div className="Card">{ArticlesList}</div>
			</div>
		);
	}
}

export default ThemesArticles;
