import React, { Component } from 'react';
import './App.css';
import { Card, Icon, Modal} from 'antd';
import Nav from './Nav'

const { Meta } = Card;

class ThemesArticles extends Component {

  constructor(props){
    super(props);
    this.state = {
      ThemeArticles: [],
      visible:false,
      title: 'No title',
      content: 'No content'
    }
  }

  showModal = (title,content) => {

    this.setState({
      visible: true,
      title: title,
      content: content
    });
  };

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  componentDidMount(){

    // You need to catch the id comming from the homePage

  }

  render() {



    return (

      <div>

            <Nav/>

            <div className="Banner"/>

            <div className="Card">

              <div  style={{display:'flex',justifyContent:'center'}}>

                <Card
                  style={{ 
                  width: 300, 
                  margin:'15px', 
                  display:'flex',
                  flexDirection: 'column',
                  justifyContent:'space-between' }}
                  cover={
                  <img
                      alt="example"
                      src='../images/alaska.jpg'
                  />
                  }
                  actions={[
                      <Icon type="read" key="ellipsis2" onClick={() => this.showModal('titre de mon article','content de mon article')}/>,
                      <Icon type="like" key="ellipsis"/>
                  ]}
                  >

                  <Meta
                    title='Article title'
                    description='Article Description'
                  />

                </Card>

                <Modal
                title={this.state.title}
                visible={this.state.visible}
                onOk={this.handleOk}
                onCancel={this.handleCancel}

                >

                  <p>{this.state.content}</p>

              </Modal>

              </div>




           </div> 



      </div>
    );
  }
}

export default ThemesArticles;
