//import React, { Component } from 'react';
import React, { useState, useEffect } from 'react';

import './App.css';
import { Input, Button } from 'antd';
import { Redirect } from 'react-router-dom';

function Login(props) {
	// constructor(props) {
	// 	super(props);
	// 	this.signUp = this.signUp.bind(this);
	// 	this.handleChange = this.handleChange.bind(this);
	// 	this.state = {
	// 		signUp_first_name: '',
	// 		signUp_last_name: '',
	// 		signUp_email: '',
	// 		signUp_password: '',
	// 		login_email: '',
	// 		login_password: '',
	// 		isUserExist: false
	// 	};
	// }
	const [ signup, setsignup ] = useState({
		first_name: '',
		last_name: '',
		email: '',
		password: ''
	});
	const [ signin, setsignin ] = useState({
		email: '',
		password: ''
	});
	const [ isUserExist, setisUserExist ] = useState(false);

	// handleChange(event) {
	// 	this.setState({ [event.target.name]: event.target.value });
	// }

	const signUp = (event) => {
		//var ctx = this;
		event.preventDefault();
		fetch(`http://localhost:3000/signup`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
			body: `first_name=${signup.first_name}&last_name=${signup.last_name}&email=${signup.email}&password=${signup.password}`
		})
			.then((response) => response.json())
			.then((data) => {
				console.log('user logué', data);
				data.isUserExist ? setisUserExist({ isUserExist: true }) : setisUserExist({ isUserExist: false });
			})
			.catch((err) => {
				console.log('erroroooor signup', err);
			});
	};

	// login(event) {
	// 	var ctx = this;
	// 	event.preventDefault();
	// 	fetch(`http://localhost:3000/signin?email=${ctx.state.login_email}&password=${ctx.state.login_password}`)
	// 		.then((response) => response.json())
	// 		.then(function(data) {
	// 			console.log('user logué', data);
	// 			data.isUserExist ? ctx.setState({ isUserExist: true }) : ctx.setState({ isUserExist: false });
	// 		})
	// 		.catch((err) => {
	// 			console.log('erroor login');
	// 		});
	// }

	if (isUserExist) {
		return <Redirect to="/homePage" />;
	} else {
		return (
			<div className="Login-page">
				{/* SIGN-UP */}
				<div className="Sign">
					<Input
						className="Login-input"
						name="singup.first_name"
						placeholder="arthur"
						onChange={(e) => setsignup({ first_name: e.target.value })}
						value={signup.first_name}
					/>
					<Input
						className="Login-input"
						name="signUp_last_name"
						placeholder="Lacapsule"
						onChange={(e) => setsignup({ last_name: e.target.value })}
						value={signup.last_name}
					/>
					<Input
						className="Login-input"
						name="signUp_email"
						placeholder="arthur@lacapsule.com"
						onChange={(e) => setsignup({ email: e.target.value })}
						value={signup.email}
					/>
					<Input.Password
						className="Login-input"
						name="signUp_password"
						placeholder="password"
						onChange={(e) => setsignup({ password: e.target.value })}
						value={signup.password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary" onClick={(e) => this.signUp(e)}>
						Sign-up
					</Button>
				</div>

				{/* SIGN-IN */}
				<div className="Sign">
					<Input
						className="Login-input"
						name="login_email"
						placeholder="arthur@lacapsule.com"
						// onChange={(e) => this.handleChange(e)}
						// value={this.state.login_email}
					/>
					<Input.Password
						className="Login-input"
						name="login_password"
						placeholder="password"
						// onChange={(e) => this.handleChange(e)}
						// value={this.state.login_password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary" onClick={(e) => this.login(e)}>
						Sign-in
					</Button>
				</div>
			</div>
		);
	}
}

export default Login;
