import React, { Component } from 'react';
import './App.css';
import { Input, Button } from 'antd';

class Login extends Component {
	constructor(props) {
		super(props);
		this.state = {
			signUp_first_name: '',
			signUp_last_name: '',
			signUp_email: '',
			signUp_password: ''
		};
	}
	signUp() {
		fetch('http://localhost:3000/signup', {
			method: 'POST',
			headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
			body: `first_name=${this.state.signUp_first_name}&last_name=${this.state.signUp_last_name}&email=${this
				.state.signUp_email}&password=${this.state.signUp_password}`
		});
	}

	render() {
		return (
			<div className="Login-page">
				{/* SIGN-IN */}
				<div className="Sign">
					<Input
						className="Login-input"
						placeholder="arthur@lacapsule.com"
						onChange={(e) => this.setState((state) => ({ signUp_email: e }))}
						value={this.state.email}
					/>
					<Input.Password
						className="Login-input"
						placeholder="password"
						onChange={(e) => this.setState((state) => ({ signUp_password: e }))}
						value={this.state.password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary">
						Sign-in
					</Button>
				</div>

				{/* SIGN-UP */}
				<div className="Sign">
					<Input
						className="Login-input"
						placeholder="arthur"
						onChange={(e) => this.setState((state) => ({ signUp_first_name: e }))}
						value={this.state.signUp_first_name}
					/>
					<Input
						className="Login-input"
						placeholder="Lacapsule"
						onChange={(e) => this.setState((state) => ({ signUp_last_name: e }))}
						value={this.state.signUp_last_name}
					/>
					<Input
						className="Login-input"
						placeholder="arthur@lacapsule.com"
						onChange={(e) => this.setState((state) => ({ signUp_email: e }))}
						value={this.state.email}
					/>
					<Input.Password
						className="Login-input"
						placeholder="password"
						onChange={(e) => this.setState((state) => ({ signUp_password: e }))}
						value={this.state.password}
					/>
					<Button href="" style={{ width: '80px' }} type="primary" onClick={signUp}>
						Sign-in
					</Button>
				</div>
			</div>
		);
	}
}

export default Login;
